[{"internalType":"uint256","name":"id","type":"uint256"}],"name":"totalSupply","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"newOwner","type":"address"}],"name":"transferOwnership","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint256","name":"tokenId","type":"uint256"}],"name":"uri","outputs":[{"internalType":"string","name":"","type":"string"}],"stateMutability":"view","type":"function"}],"devdoc":{"kind":"dev","methods":{"balanceOf(address,uint256)":{"details":"See {IERC1155-balanceOf}. Requirements: - `account` cannot be the zero address."},"balanceOfBatch(address[],uint256[])":{"details":"See {IERC1155-balanceOfBatch}. Requirements: - `accounts` and `ids` must have the same length."},"exists(uint256)":{"details":"Indicates whether any token exist with a given id, or not."},"isApprovedForAll(address,address)":{"details":"See {IERC1155-isApprovedForAll}."},"owner()":{"details":"Returns the address of the current owner."},"renounceOwnership()":{"details":"Leaves the contract without owner. It will not be possible to call `onlyOwner` functions. Can only be called by the current owner. NOTE: Renouncing ownership will leave the contract without an owner, thereby disabling any functionality that is only available to the owner."},"safeBatchTransferFrom(address,address,uint256[],uint256[],bytes)":{"details":"See {IERC1155-safeBatchTransferFrom}."},"safeTransferFrom(address,address,uint256,uint256,bytes)":{"details":"See {IERC1155-safeTransferFrom}."},"setApprovalForAll(address,bool)":{"details":"See {IERC1155-setApprovalForAll}."},"supportsInterface(bytes4)":{"details":"See {IERC165-supportsInterface}."},"totalSupply(uint256)":{"details":"Total amount of tokens in with a given id."},"transferOwnership(address)":{"details":"Transfers ownership of the contract to a new account (`newOwner`). Can only be called by the current owner."}},"version":1},"userdoc":{"kind":"user","methods":{},"version":1}},"settings":{"compilationTarget":{"project/contracts/TCF_NFTPrice.sol":"TCF_NFTPrice"},"evmVersion":"istanbul","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":200},"remappings":[]},"sources":{"project/contracts/TCF_NFT.sol":{"content":"// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts 4.9.6

pragma solidity ^0.8.1;

import {ERC1155} from "./openzeppelin_l/contracts/token/ERC1155/ERC1155.sol";
import {
    ERC1155Supply
} from "./openzeppelin_l/contracts/token/ERC1155/extensions/ERC1155Supply.sol";
import {Ownable} from "./openzeppelin_l/contracts/access/Ownable.sol";
import {
    ERC1155URIStorage
} from "./openzeppelin_l/contracts/token/ERC1155/extensions/ERC1155URIStorage.sol";

contract TCF_NFT is ERC1155, Ownable, ERC1155URIStorage, ERC1155Supply {
    constructor() {}

    // 创建6个NFT
    // 为每一类NFT设置URI
    function setTokenURI(
        uint256[] calldata tokenIds,
        bytes[] calldata URIs
    ) external onlyOwner {
        require(
            tokenIds.length != URIs.length,
            "tokenIds and URIs should have same length"
        );

        uint256 len = tokenIds.length;
        for (uint256 i = 0; i < len; ++i) {
            ERC1155URIStorage._setURI(tokenIds[i], string(URIs[i]));
        }
    }

    // 重置NFT价格

    // 为每一类NFT设置价格和动态比例
    // 设置每一类NFT的价格
    // 保存每一个NFT_ERC20的创建时间
    // 返回用户有效的NFT
    // 返回用户所有的NFT

    function mint(
        address account,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public {
        _mint(account, id, amount, data);
    }

    function mintBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public onlyOwner {
        _mintBatch(to, ids, amounts, data);
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155, ERC1155Supply) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function uri(
        uint256 tokenId
    )
        public
        view
        virtual
        override(ERC1155, ERC1155URIStorage)
        returns (string memory)
    {
        return super.uri(tokenId);
    }
}
","keccak256":"0x7a7463c852dddfae901bcf941932679991b3d7d490f1512198494c1725d9ef09","license":"MIT"},"project/contracts/TCF_NFTPrice.sol":{"content":"// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts 4.9.6

pragma solidity ^0.8.1;

import {TCF_NFT} from "./TCF_NFT.sol";

// TODO:返回或revert的string消息中标明，是那个函数
// TODO:删除initedTokenAddress，待考虑
// TODO:优化可见性，public，external，internal
// TODO:写一个错误列表，然后智能合约中的错误使用编码来表示

// 获取支持的token地址
// 1、获取支持的token数
// 2、遍历调用tokenAddress_array

// 查看某个地址是否支持
// 调用supportedTokenAmount

// 查看比例和权限时长

contract TCF_NFTPrice is TCF_NFT {
    string private constant ERR_ADDR_LEN = "ADDR_LEN";
    string private constant ERR_ADDR_EXISTS = "ADDR_EXISTS";
    string private constant ERR_NOT_TCF_USDT = "NOT_TCF_USDT";
    string private constant ERR_DUP_TCF = "DUP_TCF";
    string private constant ERR_DUP_USDT = "DUP_USDT";
    string private constant ERR_PRICES_LEN = "PRICES_LEN";
    string private constant ERR_TOKEN_UNSUPPORTED = "TOKEN_UNSUPPORTED";
    string private constant ERR_TOKENID_RANGE = "TOKENID_RANGE";
    string private constant ERR_NATURE_TOKEN_DEL = "NATURE_TOKEN_DEL";
    string private constant ERR_PRICES_INITIALIZED = "PRICES_INITIALIZED";

    // 支持的支付方式，常量TC,TCF,USDT(后期确定TCF和USDT的合约地址之后再写)
    // TC原生代币的没有地址不在这里面存放
    // 0表示不支持，1表示支持
    // 设置它的原因：用于检测设置价格时，传入的地址正确
    mapping(address => uint8) public supportTokenType;
    // 包含TC
    uint256 public supportedTokenAmount;
    // 支持的token数组
    address[] public tokenAddress_array;

    // 如果支持的token Address已经初始化，则为1，否则为0
    uint8 public initedTokenAddress;

    // 如果这个合约价格被初始化，则设为1
    uint8 NFTPrice_initialized;

    // 支付方式，这个结构用于初始化代币价格时，外部将数据传入智能合约
    // 原生代币没有地址，所以在传入时，设为空
    struct priceTypeAndAomut {
        address tokenAddress;
        uint256 amount;
    }

    // 权益：保存价格，比例，有限期
    // 可优化，使用两个map用于存储素具，一份是ratio与indate集合，另一份是价格
    // 对于某一个NFT的ratio和indate，先赋值给memory，然后一次性保存在storage，比当前少一次对storage的存储，但增加了代码量。
    // 所以还是算了
    struct equities_store {
        // 三种支付方式的价格，TC,TCF,USDT
        mapping(address => uint256) prices;
        // 动态比例，10% ,这里就为10
        uint8 ratio;
        // 有效期
        uint32 indate;
    }

    // 6种NFT的权益。如果使用mapping如果后期想要增加产品，修改合约代码量会小一些
    // NFTS使用mapping，在初始化的时候进行了大量写操作，消耗了大量gas。但在mint操作，获取对应NFT价格时，减少了便利操作，降低了gas消耗。
    // mapping不支持memory，不能设置临时变量一次性给NFTS赋值
    // 因为只有6种，所以使用uint256
    mapping(uint256 => equities_store) public NFTS;

    /***
     * @dev 初始化函数
     * @param prices 每个NFT的三种价格
     * @param tokenAddresses 支持的token 地址
     */
    //  参数前面加_,表示这是参数变量，方便看代码，没有其他语法含义
    // function NFTPrice_init(
    //     priceTypeAndAomut[][6] calldata _prices,
    //     address[] memory _tokenAddresses,
    //     bool _checkParams
    // ) public onlyOwner initializer {
    //     __ERC1155_init("");
    //     addSupportedToken(_tokenAddresses, _checkParams);
    //     initPrice(_prices, _checkParams);
    // }

    // 初始化NFT权益
    // TC代币的地址设为0
    function initPrice(
        priceTypeAndAomut[][6] calldata prices,
        bool checkParams
    ) external onlyOwner {
        require(NFTPrice_initialized == 0, ERR_PRICES_INITIALIZED);

        if (checkParams) {
            string memory code = checkInitPricesParams(prices);
            if (bytes(code).length != 0) {
                revert(code);
            }
        }

        // 有效时长，分别对应 180 200 240 280 320 360  单位是天
        // 测试的时候：天数*每天的秒
        uint32[6] memory indetes = [
            15552000,
            17280000,
            20736000,
            24192000,
            27648000,
            31104000
        ];
        // 动态比例
        uint8[6] memory ratios = [40, 50, 60, 80, 90, 100];

        // equities_parm[6] memory NFTS_;
        uint256 addressLen = prices[0].length;

        for (uint256 i = 0; i < 6; ++i) {
            // 跟新NFTS数据
            NFTS[i].indate = indetes[i];
            NFTS[i].ratio = ratios[i];
            for (uint256 j = 0; j < addressLen; j++) {
                address tokenAddress = prices[i][j].tokenAddress;
                // 如果为原生代币，地址设为0x1
                if (tokenAddress == address(0)) {
                    tokenAddress = address(0x1);
                }
                NFTS[i].prices[tokenAddress] = prices[i][j].amount;
            }
        }
        NFTPrice_initialized = 1;
    }

    // 修改单个NFT价格
    // 原生代币TC，传入空地址
    function changeNFTPrice(
        uint256 tokenID,
        address tokenAddress,
        uint256 amount
    ) external onlyOwner {
        string memory code = _checkAccessPriceDetail(tokenID, tokenAddress);
        if (bytes(code).length != 0) {
            revert(code);
        }

        if (tokenAddress == address(0)) {
            tokenAddress = address(0x1);
        }
        NFTS[tokenID].prices[tokenAddress] = amount;
    }

    // 获取价格
    // 返回的类似数组元素，第一个保存string，第二个保存uint256
    function getNFTPrice(
        uint256 tokenID,
        address tokenAddress_
    ) external view returns (string memory, uint256) {
        string memory errorMessage = _checkAccessPriceDetail(
            tokenID,
            tokenAddress_
        );
        if (bytes(errorMessage).length != 0) {
            return (errorMessage, 0);
        }

        if (tokenAddress_ == address(0)) {
            tokenAddress_ = address(0x1);
        }
        return ("", NFTS[tokenID].prices[tokenAddress_]);
    }

    function _checkAccessPriceDetail(
        uint256 tokenID,
        address tokenAddress
    ) private view returns (string memory) {
        if (tokenID > 5) {
            return ERR_TOKENID_RANGE;
        }
        if (tokenAddress != address(0) && supportTokenType[tokenAddress] != 1) {
            return _detailAddr(tokenAddress, ERR_TOKEN_UNSUPPORTED);
        }
        return "";
    }

    // 为什么要单独进行检查参数，因为如果把每个检查的目标发在不同位置进行检查，
    // 每次都需要进行遍历，需要消耗比较多的gas，所以放在一起，减少gas消耗
    // 后台需要检查的：不要传入相同的地址
    function checkInitPricesParams(
        priceTypeAndAomut[][6] calldata prices
    ) public view returns (string memory) {
        // 1、检查tokenAddresses和prices[i].priceData.length相同
        // 2、价格里面必须支持原生代币
        // 3、检查价格参数中每个NFT支持的token Address 是否在所支持的tokenAddress列表之内

        for (uint256 i = 0; i < 6; ++i) {
            // 计划支持3种代币，包括USDT,TC,TCF
            // 1、不强制要求tokenAddresses长度为2的原因：项目可能暂时不支持USDT。为什么是2，因为TC是原生代币，所以不需要传入地址
            if (prices[i].length != supportedTokenAmount) {
                return ERR_PRICES_LEN;
            }

            // 要求支付方式为支持的代币类型
            // 要求价格里面必须支持原生代币
            priceTypeAndAomut[] memory price = prices[i];
            // bool TCExist = false;
            for (uint256 j = 0; j < supportedTokenAmount; ++j) {
                // 获取购买某个NFT时，支持的某种支付方式的合约地址
                if (
                    price[j].tokenAddress != address(0) &&
                    // 在initSupportToken中已经设置
                    supportTokenType[price[j].tokenAddress] != 1
                ) {
                    return
                        _detailAddr(
                            price[j].tokenAddress,
                            ERR_TOKEN_UNSUPPORTED
                        );
                }

                // 代码量过大，删除一些不必要的检查
                // if (tokenAddress == address(0)) {
                //     if (TCExist == true) {
                //         return
                //             "Only one zero address representing TC is allowed";
                //     } else {
                //         TCExist = true;
                //     }
                // }
            }

            // if (!TCExist) {
            //     return "One zero address representing TC must exist";
            // }
        }

        return "";
    }

    // 删除支持的token，不支持删除原生代币
    function deleteSupportedToken(address oldTokenAddress) external onlyOwner {
        // 原生代币不能被删除
        require(oldTokenAddress != address(0), ERR_NATURE_TOKEN_DEL);
        // 检查就tokenAddress是否被初始化过
        require(
            supportTokenType[oldTokenAddress] == 1,
            _detailAddr(oldTokenAddress, ERR_TOKEN_UNSUPPORTED)
        );

        // 删除map列表中要删除的地址
        delete supportTokenType[oldTokenAddress];
        // supportedTokenAmount包含原生代币数，但原生token没有地址，所以tokenAddress_array元素数=supportedTokenAmount-1
        for (uint256 i = 0; i < supportedTokenAmount - 1; i++) {
            if (tokenAddress_array[i] == oldTokenAddress) {
                // supportedTokenAmount包含原生代币数，但原生代币地址为空，不保存，所以减2
                // 将最后一个元素放置在删除的位置
                tokenAddress_array[i] = tokenAddress_array[
                    supportedTokenAmount - 2
                ];
                // 删除最后一个元素
                // 必须使用pop完成删除最后一个元素，数组的数据长度减一
                // 在增加数据时，使用push，在原数据的基础上进行增加。
                // 如果赋值为空，在增加时，就会在空地址后面增加新的元素，不符合预期
                tokenAddress_array.pop();
            }
        }
        supportedTokenAmount -= 1;
    }

    // 设置支持的token
    // 设置这个函数的原因：在初始化价格时，对价格进行检查
    function addSupportedToken(
        address[] memory tokenAddresses,
        bool checkParams
    ) public onlyOwner {
        if (checkParams) {
            string memory code = _checkTokenAddressesCode(tokenAddresses);
            if (bytes(code).length != 0) {
                revert(code);
            }
        }

        uint256 len = tokenAddresses.length;
        for (uint256 i = 0; i < len; i++) {
            supportTokenType[tokenAddresses[i]] = 1;
            tokenAddress_array.push(tokenAddresses[i]);
        }

        if (initedTokenAddress == 1) {
            supportedTokenAmount += len;
        } else {
            initedTokenAddress = 1;
            supportedTokenAmount = len + 1;
        }
    }

    // 用于在操作之前进行参数检查
    function checkTokenAddressesCode(
        address[] memory _tokenAddresses
    ) external view returns (string memory) {
        return _checkTokenAddressesCode(_tokenAddresses);
    }

    // 设置支持的token时，进行参数检查
    function _checkTokenAddressesCode(
        address[] memory tokenAddresses
    ) private view returns (string memory) {
        //在修改合约地址时，也会调用这个函数，但修改合约地址时，已经初始化链外，所以不可以加初始化检查
        // if (initedTokenAddress == 1) {
        //     response.message = "Token addresses had been initialized";
        //     return response;
        // }

        uint256 len = tokenAddresses.length;
        // 检查数据长度
        if (len > 2) {
            return ERR_ADDR_LEN;
        }

        // 检查是否为TCF或USDT,在检查是否合约地址之后
        bool TCFExist;
        bool USDTExist;
        for (uint256 i = 0; i < len; i++) {
            // 检查地址是否已经存在
            if (supportTokenType[tokenAddresses[i]] == 1) {
                return _detailAddr(tokenAddresses[i], ERR_ADDR_EXISTS);
            }

            // 检查是否为TCF合约和USDT合约，没有继续操作是因为，在继续之前需要检查这两个合约是否为erc20合约
            // 不可以通过erc163合约来实现（USDT没有实现erc163），直接使用call调用
            bytes memory callData = abi.encodeWithSignature("symbol()");
